import React from "react";
import clsx from "clsx";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import List from "@material-ui/core/List";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import ChevronRightIcon from "@material-ui/icons/ChevronRight";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import HomeIcon from "@material-ui/icons/Home";
import ExploreIcon from "@material-ui/icons/Explore";
import GitHubIcon from "@material-ui/icons/GitHub";
import { Link } from "react-router-dom";

const drawerWidth = 260;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  appBar: {
    background: "linear-gradient(135deg, #6a11cb, #2575fc)",
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    color: "#fff",
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: "none",
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
    background: "#1c1c1c",
    color: "#fff",
    boxShadow: "4px 0px 10px rgba(0,0,0,0.3)",
  },
  drawerHeader: {
    display: "flex",
    alignItems: "center",
    padding: theme.spacing(0, 1),
    ...theme.mixins.toolbar,
    justifyContent: "space-between",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
  sidebarButton: {
    color: "#fff",
    padding: "12px 20px",
    display: "flex",
    alignItems: "center",
    borderRadius: "5px",
    margin: "5px 15px",
    "&:hover": {
      background: "#333",
      transition: "0.3s",
    },
  },
  sidebarIcon: {
    marginRight: 10,
  },
  githubFooter: {
    width: "100%",
    height: "70px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    borderTop: "1px solid #444",
  },
}));

export default function PersistentDrawerLeft({ pageTitle, navItems, children }) {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(true);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        position="fixed"
        className={clsx(classes.appBar, { [classes.appBarShift]: open })}
      >
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            className={clsx(classes.menuButton, open && classes.hide)}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap style={{ display: "flex", alignItems: "center" }}>
            <img
              alt="logo"
              src="/logo.png"
              style={{ height: "40px", width: "auto", marginRight: 10 }}
            />
            SecureChain
          </Typography>
        </Toolbar>
      </AppBar>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={open}
        classes={{ paper: classes.drawerPaper }}
      >
        <div className={classes.drawerHeader}>
          <Typography variant="h6">{pageTitle}</Typography>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === "ltr" ? <ChevronLeftIcon style={{ color: "#fff" }} /> : <ChevronRightIcon />}
          </IconButton>
        </div>
        <List>
          <Link to="/" style={{ textDecoration: "none" }}>
            <ListItem button className={classes.sidebarButton}>
              <HomeIcon className={classes.sidebarIcon} />
              <ListItemText primary="Home" />
            </ListItem>
          </Link>
          <Link to="/explorer" style={{ textDecoration: "none" }}>
            <ListItem button className={classes.sidebarButton}>
              <ExploreIcon className={classes.sidebarIcon} />
              <ListItemText primary="Explorer" />
            </ListItem>
          </Link>
        </List>
        <List>
          {navItems.length !== 0 &&
            navItems.map((item) => (
              <Link to={item[1]} style={{ textDecoration: "none" }} key={item[0]}>
                <ListItem button className={classes.sidebarButton}>
                  <ListItemText primary={item[0]} />
                </ListItem>
              </Link>
            ))}
        </List>
        <div className={classes.githubFooter}>
          <a href="#" style={{ textDecoration: "none" }}>
            <GitHubIcon style={{ color: "#fff" }} />
          </a>
        </div>
      </Drawer>
      <main className={clsx(classes.content, { [classes.contentShift]: open })}>
        <div className={classes.drawerHeader} />
        <div style={{ margin: "0 auto", maxWidth: 1300 }}>{children}</div>
      </main>
    </div>
  );
}
